export async function seedPart1() {
  console.log('\nüå± Part 1: Seeding A1 General Topics (461 words, 12 topics)...');

  // Clear old data
  await prisma.topicWord.deleteMany({});
  await prisma.topicProgress.deleteMany({});
  await prisma.gameAnswer.deleteMany({});
  await prisma.progress.deleteMany({});
  await prisma.history.deleteMany({});
  await prisma.favorite.deleteMany({});
  await prisma.topic.deleteMany({});
  await prisma.word.deleteMany({});
  console.log('  ‚úì Cleared old data');

  // Create topics
  for (const topicData of A1_TOPICS) {
    await prisma.topic.create({ data: topicData });
  }
  console.log('  ‚úì Created 12 topics');

  // Create words and link to topics
  let wordCount = 0;
  for (const topicData of A1_TOPICS) {
    const topic = await prisma.topic.findUnique({ where: { slug: topicData.slug } });
    if (!topic) continue;
    const words = TOPIC_WORDS[topicData.slug] || [];
    let order = 1;
    for (const w of words) {
      let word = await prisma.word.findFirst({ where: { word: w.word, article: w.article } });
      if (!word) {
        word = await prisma.word.create({
          data: {
            word: w.word, article: w.article, gender: w.gender,
            plural: w.plural, translationEn: w.translationEn,
            translationVi: w.translationVi, category: topicData.slug,
            level: 'A1', examples: w.examples, tips: w.tips,
          },
        });
        wordCount++;
      }
      await prisma.topicWord.create({
        data: { topicId: topic.id, wordId: word.id, isCore: w.isCore || false, order: order++ },
      });
    }
    await prisma.topic.update({ where: { id: topic.id }, data: { wordCount: words.length } });
    console.log(`  ‚úì ${topicData.slug}: ${words.length} words`);
  }
  console.log(`\n‚úÖ Part 1 done: ${wordCount} unique words created`);
}

// Run directly
if (require.main === module) {
  seedPart1()
    .catch(e => { console.error('‚ùå Error:', e); process.exit(1); })
    .finally(() => prisma.$disconnect());
}